window.main.apis.renderFontList=new function(){this.run=function(t,n){var e=t.fontlist,a=twig({data:$("#template-fontlist-listitem").html()}),i=$(".font-list");$(".contents").append(i);var l=""+$("#mainform input[name=sampleText]").val();for(var r in e){var o=e[r];o.sampleText=l,o.sampleText.length||(o.sampleText=o.family);var p=$(a.render({font:o}));p.find(".font-list__family a").click(function(){$(this).parent().parent().parent().find(".font-list__property").toggle()}),i.append(p)}}};